let nav = document.querySelector(".navbar");
(window.onscroll = function () {
	document.documentElement.scrollTop > 20
		? nav.classList.add("header-scrolled")
		: nav.classList.remove("header-scrolled");
}),
	jQuery(function (t) {
		t(".navbar .dropdown").hover(
			function () {
				t(this)
					.find(".dropdown-menu")
					.first()
					.stop(!0, !0)
					.delay(250)
					.slideDown();
			},
			function () {
				t(this)
					.find(".dropdown-menu")
					.first()
					.stop(!0, !0)
					.delay(100)
					.slideUp();
			}
		),
			t(".navbar .dropdown > a").click(function () {
				location.href = this.href;
			});
	});
let navBar = document.querySelectorAll(".nav-link"),
	navCollapse = document.querySelector(".navbar-collapse.collapse");
navBar.forEach(function (t) {
	t.addEventListener("click", function () {
		navCollapse.classList.remove("show");
	});
}),
	$(document).ready(function () {
		$(window).scroll(function () {
			$(this).scrollTop() > 100
				? $("#scroll").fadeIn()
				: $("#scroll").fadeOut();
		}),
			$("#scroll").click(function () {
				return $("html, body").animate({ scrollTop: 0 }, 600), !1;
			});
	}),
	$(".home-banner-slider").slick({
		dots: !0,
		arrows: !1,
		infinite: !0,
		speed: 1e3,
		slidesToShow: 1,
		slidesToScroll: 1,
	}),
	AOS.init();
const TypeWriter = function (t, e, i = 3e3) {
	(this.txtElement = t),
		(this.words = e),
		(this.txt = ""),
		(this.wordIndex = 0),
		(this.wait = parseInt(i, 10)),
		this.type(),
		(this.isDeleting = !1);
};
function init() {
	const t = document.querySelector(".txt-type"),
		e = JSON.parse(t.getAttribute("data-words")),
		i = t.getAttribute("data-wait");
	new TypeWriter(t, e, i);
}
(TypeWriter.prototype.type = function () {
	const t = this.wordIndex % this.words.length,
		e = this.words[t];
	this.isDeleting
		? (this.txt = e.substring(0, this.txt.length - 1))
		: (this.txt = e.substring(0, this.txt.length + 1)),
		(this.txtElement.innerHTML = `<span class="txt text-highlight">${this.txt}</span>`);
	let i = 300;
	const s = document.querySelector(".fas-home");
	this.isDeleting && (i /= 4),
		this.isDeleting
			? (s.className = "fas fas-home fa-pencil-alt erasing-animation")
			: (s.className = "fas fas-home fa-pencil-alt writing-animation"),
		this.isDeleting || this.txt !== e
			? this.isDeleting &&
			  "" === this.txt &&
			  ((this.isDeleting = !1), this.wordIndex++, (i = 1e3))
			: ((i = this.wait),
			  (this.isDeleting = !0),
			  (s.className = "fas fas-home fa-pencil-alt")),
		setTimeout(() => this.type(), i);
}),
	document.addEventListener("DOMContentLoaded", init),
	$(".portfolio-client-slider").slick({
		dots: !0,
		arrows: !0,
		infinite: !1,
		prevArrow: $(".portfolio-client-slider-prev"),
		nextArrow: $(".portfolio-client-slider-next"),
		speed: 300,
		slidesToShow: 4,
		slidesToScroll: 4,
		responsive: [
			{ breakpoint: 1024, settings: { slidesToShow: 3, slidesToScroll: 3 } },
			{ breakpoint: 600, settings: { slidesToShow: 2, slidesToScroll: 2 } },
			{ breakpoint: 480, settings: { slidesToShow: 1, slidesToScroll: 1 } },
		],
	});

$(document).ready(function ($) {
	//Check if an element was in a screen
	function isScrolledIntoView(elem) {
		var docViewTop = $(window).scrollTop();
		var docViewBottom = docViewTop + $(window).height();
		var elemTop = $(elem).offset().top;
		var elemBottom = elemTop + $(elem).height();
		return elemBottom <= docViewBottom;
	}
	//Count up code
	function countUp() {
		$(".counter").each(function () {
			var $this = $(this), // <- Don't touch this variable. It's pure magic.
				countTo = $this.attr("data-count");
			ended = $this.attr("ended");

			if (ended != "true" && isScrolledIntoView($this)) {
				$({ countNum: $this.text() }).animate(
					{
						countNum: countTo,
					},
					{
						duration: 2500, //duration of counting
						easing: "swing",
						step: function () {
							$this.text(Math.floor(this.countNum));
						},
						complete: function () {
							$this.text(this.countNum);
						},
					}
				);
				$this.attr("ended", "true");
			}
		});
	}
	//Start animation on page-load
	if (isScrolledIntoView(".counter")) {
		countUp();
	}
	//Start animation on screen
	$(document).scroll(function () {
		if (isScrolledIntoView(".counter")) {
			countUp();
		}
	});
});

$(document).ready(function () {
	$(window).on("load", function () {
		$(".loader").fadeOut();
	});
});
